#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\hello\projects\freemark-demo\node_modules\.pnpm\freemarker.js@1.4.0\node_modules\freemarker.js\fmpp\node_modules;C:\hello\projects\freemark-demo\node_modules\.pnpm\freemarker.js@1.4.0\node_modules\freemarker.js\node_modules;C:\hello\projects\freemark-demo\node_modules\.pnpm\freemarker.js@1.4.0\node_modules;C:\hello\projects\freemark-demo\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/hello/projects/freemark-demo/node_modules/.pnpm/freemarker.js@1.4.0/node_modules/freemarker.js/fmpp/node_modules:/mnt/c/hello/projects/freemark-demo/node_modules/.pnpm/freemarker.js@1.4.0/node_modules/freemarker.js/node_modules:/mnt/c/hello/projects/freemark-demo/node_modules/.pnpm/freemarker.js@1.4.0/node_modules:/mnt/c/hello/projects/freemark-demo/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir//bin/sh$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir//bin/sh$exe"  "$basedir/../freemarker.js/fmpp/fmpp" $args
  } else {
    & "$basedir//bin/sh$exe"  "$basedir/../freemarker.js/fmpp/fmpp" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "/bin/sh$exe"  "$basedir/../freemarker.js/fmpp/fmpp" $args
  } else {
    & "/bin/sh$exe"  "$basedir/../freemarker.js/fmpp/fmpp" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
